/*** Preloader ***/

.wrapper-landing #preloader {
  position: fixed;
  z-index: 1111111111111;
  top: 0;
  left: 0;
  display: block;
  width: 100%;
  height: 100%;
  background-color: #111;
}

.wrapper-landing #preloader-content {
  width: 200px;
  margin: 0 auto;
  display: block;
  text-align: center;
  top: 50%;
  position: absolute;
  left: 50%;
  -webkit-transform: translate(-50%, -50%) rotate(0deg);
  transform: translate(-50%, -50%) rotate(0deg);
}

.wrapper-landing .path {
  stroke-dasharray: 2350;
  stroke-dashoffset: 910;
  stroke-width: 2;
  -webkit-animation: dash 3s linear infinite;
  animation: dash 3s linear infinite;
  fill-opacity: 0;
}

.wrapper-landing .path2 {
  stroke-dasharray: 2350;
  stroke-dashoffset: 910;
  stroke-width: 2;
  -webkit-animation: dash 6s linear infinite;
  animation: dash 6s linear infinite;
  fill-opacity: 0;
}

