.wrapper-landing .pricing-switcher > p {
  display: inline-block;
  position: relative;
  padding: 0;
  padding-right: 4px;
  border-radius: 0;
  background: #111;
  border: 1px solid #333;
}

.wrapper-landing .pricing-switcher input[type="radio"] {
  position: absolute;
  opacity: 0;
}

.wrapper-landing .pricing-switcher label {
  position: relative;
  z-index: 1;
  display: inline-block;
  float: left;
  width: 105px;
  height: 39px;
  line-height: 43px;
  cursor: pointer;
  padding-left: 8px;
  font-size: 16px;
  color: #fff;
}

.wrapper-landing .pricing-switcher label.switch-1.active,
.pricing-switcher label.switch-2.active {
  color: #fff;
}

.wrapper-landing .pricing-switcher .switch {
  position: absolute;
  top: 2px;
  left: 2px;
  height: 40px;
  width: 105px;
  border-radius: 0;
  transition: -webkit-transform 0.5s;
  transition: transform 0.5s;
  transition: transform 0.5s, -webkit-transform 0.5s;
}

.wrapper-landing .pricing-switcher input[type="radio"]:checked + label + .switch,
.pricing-switcher input[type="radio"]:checked + label:nth-of-type(n) + .switch {
  -webkit-transform: translateX(105px);
  transform: translateX(105px);
}

.wrapper-landing .no-js .pricing-switcher {
  display: none;
}

.wrapper-landing .pricing-list {
  margin: 32px 0 0;
  list-style-type: none;
}

.wrapper-landing .pricing-list > li {
