  margin-bottom: 16px;
}

.wrapper-landing .pricing-wrapper {
  position: relative;
  list-style-type: none;
  padding: 0;
}

.wrapper-landing .touch .pricing-wrapper {
  -webkit-perspective: 2000px;
  perspective: 2000px;
}

.wrapper-landing .pricing-wrapper.is-switched .is-visible {
  -webkit-transform: rotateY(180deg);
  transform: rotateY(180deg);
  -webkit-animation: rotate 0.5s;
  animation: rotate 0.5s;
}

.wrapper-landing .pricing-wrapper.is-switched .is-hidden {
  -webkit-transform: rotateY(0);
  transform: rotateY(0);
  -webkit-animation: rotate-inverse 0.5s;
  animation: rotate-inverse 0.5s;
  opacity: 0;
}

.wrapper-landing .pricing-wrapper.is-switched .is-selected {
  opacity: 1;
}

.wrapper-landing .pricing-wrapper.is-switched.reverse-animation .is-visible {
  -webkit-transform: rotateY(-180deg);
  transform: rotateY(-180deg);
  -webkit-animation: rotate-back 0.5s;
  animation: rotate-back 0.5s;
}

.wrapper-landing .pricing-wrapper.is-switched.reverse-animation .is-hidden {
  -webkit-transform: rotateY(0);
  transform: rotateY(0);
  -webkit-animation: rotate-inverse-back 0.5s;
  animation: rotate-inverse-back 0.5s;
  opacity: 0;
}

.wrapper-landing .pricing-wrapper.is-switched.reverse-animation .is-selected {
  opacity: 1;
}

.wrapper-landing .pricing-wrapper > li {
  background-color: #1d1d1d;
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  outline: 1px solid transparent;
}

.wrapper-landing .pricing-wrapper > li::after {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  height: 100%;
  width: 50px;
