@-webkit-keyframes style__rotate_23Mma {
  0% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(200deg);
    transform: perspective(2000px) rotateY(200deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(180deg);
    transform: perspective(2000px) rotateY(180deg);
  }
}

@keyframes style__rotate_23Mma {
  0% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(200deg);
    transform: perspective(2000px) rotateY(200deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(180deg);
    transform: perspective(2000px) rotateY(180deg);
  }
}

@-webkit-keyframes style__rotate-inverse_1HMKu {
  0% {
    -webkit-transform: perspective(2000px) rotateY(-180deg);
    transform: perspective(2000px) rotateY(-180deg);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(20deg);
    transform: perspective(2000px) rotateY(20deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }
}

@keyframes style__rotate-inverse_1HMKu {
  0% {
    -webkit-transform: perspective(2000px) rotateY(-180deg);
    transform: perspective(2000px) rotateY(-180deg);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(20deg);
    transform: perspective(2000px) rotateY(20deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }
}

@-webkit-keyframes style__rotate-back_3ouky {
  0% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(-200deg);
    transform: perspective(2000px) rotateY(-200deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(-180deg);
    transform: perspective(2000px) rotateY(-180deg);
  }
}

@keyframes style__rotate-back_3ouky {
  0% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(-200deg);
    transform: perspective(2000px) rotateY(-200deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(-180deg);
    transform: perspective(2000px) rotateY(-180deg);
  }
}

@-webkit-keyframes style__rotate-inverse-back_3Fd4X {
  0% {
    -webkit-transform: perspective(2000px) rotateY(180deg);
    transform: perspective(2000px) rotateY(180deg);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(-20deg);
    transform: perspective(2000px) rotateY(-20deg);
  }

  100% {
    -webkit-transform: perspective(2000px) rotateY(0);
    transform: perspective(2000px) rotateY(0);
  }
}

@keyframes style__rotate-inverse-back_3Fd4X {
  0% {
    -webkit-transform: perspective(2000px) rotateY(180deg);
    transform: perspective(2000px) rotateY(180deg);
  }

  70% {
    /* this creates the bounce effect */
    -webkit-transform: perspective(2000px) rotateY(-20deg);
    transform: perspective(2000px) rotateY(-20deg);
  }

  100% {
